<template>
  <ion-card>
    <ion-button expand="block" @click="SendMsg">카메라</ion-button>
    <img :src="movie" />
  </ion-card>
</template>

<script>
import io from 'socket.io-client'

export default {
  name: 'Camera',
  data () {
    return {
      Server: this.$store.state.server,
      movie: null,
      socket: ''
    }
  },
  created () {
    this.ConnectSocket()
  },
  mounted () {

  },
  methods: {
    ConnectSocket () {
      this.socket = io(this.Server)
      this.socket.on('Mobile', data => {
        this.movie = data
      })
    },
    async SendMsg () {
      let message =
        'data:image/jpeg;base64,'
      await this.socket.emit('Camera', message)
    }

  }
}
</script>

